import { Component } from '@angular/core';
import { Task } from 'src/app/model/task';
import { CrudService } from 'src/app/service/crud.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {

  taskObj: Task = new Task();
  // taskArr: Task[] = [];
  completedTaskArr: Task[] = [];
  incompleteTaskArr: Task[] = [];

  addTaskValue: string = '';
  addTaskDescription:string = '';
  addDueDate: Date | null = null;
  editTaskValue: string = '';
  editTaskDescription:string = '';
  editDueDate: Date | null = null;

  constructor(private crudService: CrudService) { }

  ngOnInit(): void {
    this.addTaskDescription = '';
    this.editTaskDescription = '';
    this.addDueDate = null;
    this.editDueDate = null;
    this.editTaskValue = '';
    this.addTaskValue = '';
    this.taskObj = new Task();
    // this.getAllTask();
    this.getCompletedTasks();
    this.getIncompleteTasks();
  }

  // getAllTask() {
  //   this.crudService.getAllTasks().subscribe(res => {
  //     this.taskArr = res;
  //   }, err => {
  //     alert("Unable to get the list of tasks");
  //   })
  // }

  addTask() {
    this.taskObj.title = this.addTaskValue; // Update to use the 'title' field
    this.taskObj.description = this.addTaskDescription;
    this.taskObj.dueDate = this.addDueDate;
    this.crudService.addTask(this.taskObj).subscribe(res => {
      alert(this.addTaskDescription);
      this.ngOnInit();
      this.addTaskValue = '';
      this.addDueDate = null;
      this.addTaskDescription = '';
    }, err => {
      alert(err);
    })
  }

  editTask() {
    this.taskObj.title = this.editTaskValue; // Update to use the 'title' field
    this.crudService.editTask(this.taskObj).subscribe(res => {
      this.ngOnInit();
    }, err => {
      alert("Failed to update task");
    })
  }

  deleteTask(etask: Task) {
    this.crudService.deleteTask(etask).subscribe(res => {
      this.ngOnInit();
    }, err => {
      alert("Failed to delete task")
    })
  }

  call(etask: Task) {
    this.taskObj = etask;
    this.editTaskValue = etask.title; // Update to use the 'title' field
  }

  completeTask(etask: Task) {
    this.crudService.completeTask(etask).subscribe(res => {
      this.ngOnInit();
      this.deleteIncompleteTask(etask);
    }, err => {
      alert("Failed to mark task as completed");
    });
  }

  incompleteTask(etask: Task) {
    this.crudService.incompleteTask(etask).subscribe(res => {
      this.ngOnInit();
      // this.deleteCompletedTask(etask);
    }, err => {
      alert("Failed to mark task as incomplete");
    });
  }
  deleteCompletedTask(etask: Task) {
    this.crudService.deleteCompletedTask(etask).subscribe(res => {
      this.ngOnInit();
    }, err => {
      alert("Failed to delete completed task");
    });
  }

  deleteIncompleteTask(etask: Task) {
    this.crudService.deleteIncompleteTask(etask).subscribe(res => {
      this.ngOnInit();
    }, err => {
      alert("Failed to delete incomplete task");
    });
  }

  getCompletedTasks() {
    this.crudService.getCompletedTasks().subscribe(res => {
      this.completedTaskArr = res;
    }, err => {
      alert("Unable to get completed tasks");
    });
  }

  getIncompleteTasks() {
    this.crudService.getIncompleteTasks().subscribe(res => {
      this.incompleteTaskArr = res;
    }, err => {
      alert("Unable to get incomplete tasks");
    });
  }
}
